mapper.update:
UPDATE quiz
SET content = #{content}
WHERE id = #{id}

mapper.select:
SELECT id,title,quiz_enum
FROM quiz
WHERE deleted = 'UNDELETE' and quiz_enum = #{quizEnum}
ORDER BY created_at DESC
LIMIT #{offset}, #{limit}

SELECT id,title,content,answer,quiz_enum
FROM quiz
WHERE deleted = 'UNDELETE' and id = #{id}
